<?php        declare(strict_types = 1);    namespace App\Model\Admin\AdministratorManage;    use App\Model\Database\DbManage;    use App\Model\DevProblems\DevProblemsManage;    require_once('C:\wamp\www\forum\back\controllers\administratorManageCtrl\pagination\getUserPaginationCtrl.php');    /**require_once('../../../controllers/administratorManageCtrl/pagination/getUserPaginationCtrl.php'); */    require_once('C:\wamp\www\forum\back\controllers\administratorManageCtrl\pagination\getTopicPaginationCtrl.php');    /** require_once('../../../controllers/administratorManageCtrl/pagination/getTopicPaginationCtrl.php'); */    require_once('C:\wamp\www\forum\back\controllers\administratorManageCtrl\pagination\getCommentPaginationCtrl.php');    /** require_once('../../../controllers/administratorManageCtrl/pagination/getCommentPaginationCtrl.php'); */    require_once('C:\wamp\www\forum\back\controllers\devProblemCtrl\pagination\screenPaginationCtrl.php');    /** require_once('../../../controllers/devProblemCtrl/pagination/screenPaginationCtrl.php'); */    class GetAllTables extends DbManage    {        /**         * get all entries of users table         */        public function getUsers(): array        {            $db = $this->dbConnect();            $queryGetUsers = $db->prepare('SELECT *, DATE_FORMAT(registration_date_user, "%d/%m/%Y à %Hh:%imin:%ss") as registration_dateFr FROM users LIMIT '.getStartPagination().', '.PaginationForAdmin::$user_par_page );            $queryGetUsers->execute(array());            $getting_users = $queryGetUsers->fetchAll();            if(count($getting_users) == 0) {                header('Location: ../../../../front/view/admin/adminManage/users/userSavedList.php');            }            return $getting_users;        }        /**         * get all entries of f_subject table        */        public function getSubjects(): array        {            $db = $this->dbConnect();            $queryGetSubjects = $db->prepare('SELECT *, DATE_FORMAT(date_added, "%d/%m/%Y à %Hh:%imin:%ss") as dateFr FROM f_subject  LIMIT '.getStartTopicsPagination().', '.PaginationForAdmin::$topic_par_page);            $queryGetSubjects->execute(array());            $getting_subjects = $queryGetSubjects->fetchAll();            if(count($getting_subjects) == 0) {                header('Location: ../../../front/view/admin/adminManage/topics/topicSavedList.php');            }            return $getting_subjects;        }        /**         * get all entries of comments table        */        public function getComments(): array        {            $db = $this->dbConnect();            $queryGetComments = $db->prepare('SELECT *, DATE_FORMAT(date_comment, "%d/%m/%Y à %Hh:%imin:%ss") as dateFr FROM f_comments LIMIT '.getStartCommentPagination().', '.PaginationForAdmin::$comment_par_page);            $queryGetComments->execute(array());            $getting_comments = $queryGetComments->fetchAll();            if(count($getting_comments) == 0){                header('Location: ../../../front/view/admin/adminManage/comments/commentSavedList.php');            }            return $getting_comments;        }        /**         * get all entries of screens table         */        public function getScreens(): array        {            $db = $this->dbConnect();            $queryGetScreens = $db->prepare('SELECT * FROM f_screens LIMIT '.getStartScreenPagination().', '.DevProblemsManage::$screen_by_page);            $queryGetScreens->execute(array());            $getting_screen = $queryGetScreens->fetchAll();            if(count($getting_screen) == 0) {                header('Location: ../../../front/view/admin/adminManage/screens/screenSavedList.php');            }            return $getting_screen;        }    }